$(document).ready(function() {
  //load画面
  loadImg();
});
window.onload = function() {
  $(function() {
    $('.loading-bg').fadeOut(1500, function() {isLoaded = true;});
  })
}

var deg = 0;
var isLoaded = false;
function loadImg() {
  deg += 180;
  $('.loading-img').css('transform', 'rotate('+ deg + 'deg)');
  if(isLoaded){
    exit;
  }
  setTimeout(loadImg,1700);
}
